                if (!isset($modx->map[$class])) {
                    $modx->loadClass($class);
                }
                if (isset($modx->map[$class])) {
                    foreach ($map as $key => $values) {
                        $modx->map[$class][$key] = array_merge($modx->map[$class][$key], $values);
                    }
                }
            }
