        $isAjax = !empty($_SERVER[\'HTTP_X_REQUESTED_WITH\']) && $_SERVER[\'HTTP_X_REQUESTED_WITH\'] == \'XMLHttpRequest\';
        if ($modx->getOption(\'friendly_urls\') && $isAjax && isset($_SERVER[\'HTTP_REFERER\'])){
