    case \'OnLoadWebDocument\':
        $q = $modx->newQuery(\'localizatorContent\');
        $q->leftJoin(\'localizatorLanguage\',\'localizatorLanguage\', \'localizatorLanguage.key = localizatorContent.key\');
        $q->where(array(
